COPY (SELECT to_char(c.fecha,'DD-MM-YYYY') as fecha_emision,
                            (SELECT to_char(max(fecha_venc),'DD-MM-YYYY') FROM finanzas.cobros where id_contrato=c.id) as fecha_venc,
                            '' as cta_ctble,
                            coalesce(c.arancel_efectivo,0)+coalesce(c.arancel_pagare_coleg,0)+coalesce(c.arancel_cheque,0)+coalesce(c.arancel_tarjeta_credito,0) as monto,
                            coalesce(va.rut,vp.rut) as rut,coalesce(va.nombre,vp.nombre) as razon_social,
                            c.id as nro_contrato,pc.id as nro_pagare 
                     FROM finanzas.contratos AS c 
                     LEFT JOIN vista_contratos AS vc USING (id)
                     LEFT JOIN vista_alumnos AS va ON va.id=c.id_alumno 
                     LEFT JOIN vista_pap     AS vp ON vp.id=c.id_pap 
                     LEFT JOIN finanzas.pagares_colegiatura AS pc ON pc.id_contrato=c.id
                     LEFT JOIN carreras        AS car ON car.id=c.id_carrera
                     WHERE (lower(pap.nombres||' '||pap.apellidos) ~* '' OR  pap.rut ~* '' OR lower(a.nombres||' '||a.apellidos) ~* '' OR  a.rut ~* '' OR lower(av.rf_nombres||' '||av.rf_apellidos) ~* '' OR  av.rf_rut ~* '' OR  text(c.id) ~* '' ) AND (lower(pap.nombres||' '||pap.apellidos) ~* 'c[aá]st[ií]ll[oó]' OR  pap.rut ~* 'c[aá]st[ií]ll[oó]' OR lower(a.nombres||' '||a.apellidos) ~* 'c[aá]st[ií]ll[oó]' OR  a.rut ~* 'c[aá]st[ií]ll[oó]' OR lower(av.rf_nombres||' '||av.rf_apellidos) ~* 'c[aá]st[ií]ll[oó]' OR  av.rf_rut ~* 'c[aá]st[ií]ll[oó]' OR  text(c.id) ~* 'c[aá]st[ií]ll[oó]' ) AND (lower(pap.nombres||' '||pap.apellidos) ~* 'c[aá][aá]m[aá]ñ[oó]' OR  pap.rut ~* 'c[aá][aá]m[aá]ñ[oó]' OR lower(a.nombres||' '||a.apellidos) ~* 'c[aá][aá]m[aá]ñ[oó]' OR  a.rut ~* 'c[aá][aá]m[aá]ñ[oó]' OR lower(av.rf_nombres||' '||av.rf_apellidos) ~* 'c[aá][aá]m[aá]ñ[oó]' OR  av.rf_rut ~* 'c[aá][aá]m[aá]ñ[oó]' OR  text(c.id) ~* 'c[aá][aá]m[aá]ñ[oó]' ) AND (lower(pap.nombres||' '||pap.apellidos) ~* 'j[aá]v[ií][eé]r[aá]' OR  pap.rut ~* 'j[aá]v[ií][eé]r[aá]' OR lower(a.nombres||' '||a.apellidos) ~* 'j[aá]v[ií][eé]r[aá]' OR  a.rut ~* 'j[aá]v[ií][eé]r[aá]' OR lower(av.rf_nombres||' '||av.rf_apellidos) ~* 'j[aá]v[ií][eé]r[aá]' OR  av.rf_rut ~* 'j[aá]v[ií][eé]r[aá]' OR  text(c.id) ~* 'j[aá]v[ií][eé]r[aá]' ) AND (lower(pap.nombres||' '||pap.apellidos) ~* '[ií]gn[aá]c[ií][aá]' OR  pap.rut ~* '[ií]gn[aá]c[ií][aá]' OR lower(a.nombres||' '||a.apellidos) ~* '[ií]gn[aá]c[ií][aá]' OR  a.rut ~* '[ií]gn[aá]c[ií][aá]' OR lower(av.rf_nombres||' '||av.rf_apellidos) ~* '[ií]gn[aá]c[ií][aá]' OR  av.rf_rut ~* '[ií]gn[aá]c[ií][aá]' OR  text(c.id) ~* '[ií]gn[aá]c[ií][aá]' ) 
                     ORDER BY c.fecha DESC ) to stdout WITH CSV HEADER